using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;using Terraria.ObjectData;namespace RecolorMod.Tiles{	public class Bambistone : ModTile	{		public override void SetStaticDefaults()		{			AddMapEntry(new Color(100, 255, 0));			Main.tileSolid[Type] = true;			Main.tileMergeDirt[Type] = true;			Main.tileBlockLight[Type] = true;			TileID.Sets.Conversion.Stone[Type] = true;			//ItemDrop = Mod.Find<ModItem>("ChunkstoneBlock").Type;            SoundType = SoundID.Tink;            SoundStyle = 1;            MinPick = 300;		}	}}